import React, { useEffect, useState } from "react";
import axios from "axios";
import CircularProgress from "@material-ui/core/CircularProgress";

const Dashboard: React.FC = () => {
  const [watchlist, setWatchlist] = useState<string[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [stockData, setStockData] = useState<{
    [symbol: string]: { name: string; price: number };
  }>({});
  const [newSymbol, setNewSymbol] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem("token");
        //console.log(token);
        const response = await axios.get(
          "http://localhost:5000/api/watchlist",
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        if (Array.isArray(response.data.watchlist)) {
          setWatchlist(response.data.watchlist);
          setLoading(false);
        } else {
          setError("Invalid data format for watchlist");
          setLoading(false);
        }
      } catch (error) {
        setError("Failed to fetch watchlist");
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  useEffect(() => {
    const fetchStockData = async () => {
      try {
        const token = localStorage.getItem("token");
        const allSymbols = [...watchlist, newSymbol]; // Concatenate new symbol with existing symbols
        const promises = allSymbols.map(async (symbol) => {
          const response = await axios.get(`/api/stock/${symbol}`, {
            headers: { Authorization: `Bearer ${token}` },
          });
          return { symbol, data: response.data };
        });
        const data = await Promise.all(promises);

        const stockInfo = data.reduce((acc, { symbol, data }) => {
          acc[symbol] = {
            name: data["Meta Data"]["2. Symbol"],
            price: parseFloat(
              data["Time Series (1min)"][
                Object.keys(data["Time Series (1min)"])[0]
              ]["4. close"]
            ),
          };
          return acc;
        }, {} as { [symbol: string]: { name: string; price: number } });
        setStockData(stockInfo);
      } catch (error) {
        setError("Failed to fetch stock information");
      }
    };
    if (watchlist.length > 0 || newSymbol !== "") {
      fetchStockData();
    }
  }, [watchlist, newSymbol]);

  const addToWatchlist = async () => {
    try {
      const token = localStorage.getItem("token");
      await axios.post(
        "http://localhost:5000/api/watchlist",
        { symbol: newSymbol },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setWatchlist((prevWatchlist) => [...prevWatchlist, newSymbol]);
      setNewSymbol("");
    } catch (error) {
      setError("Failed to add symbol to watchlist");
    }
  };

  const removeFromWatchlist = async (symbol: string) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`/api/watchlist/${symbol}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setWatchlist((prevWatchlist) =>
        prevWatchlist.filter((item) => item !== symbol)
      );
    } catch (error) {
      setError("Failed to remove symbol from watchlist");
    }
  };

  return (
    <div>
      <h1>Dashboard</h1>
      <div>
        <h2>Add Symbol to Watchlist</h2>
        <input
          type="text"
          value={newSymbol}
          onChange={(e) => setNewSymbol(e.target.value)}
        />
        <button onClick={addToWatchlist}>Add</button>
      </div>
      {loading ? (
        <CircularProgress />
      ) : error ? (
        <p>{error}</p>
      ) : (
        <div>
          <h2>Stock Information</h2>
          <table>
            <thead>
              <tr>
                <th>Symbol</th>
                <th>Name</th>
                <th>Price</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {watchlist.map((symbol) => (
                <tr key={symbol}>
                  <td>{symbol}</td>
                  <td>{stockData[symbol]?.name}</td>
                  <td>{stockData[symbol]?.price}</td>
                  <td>
                    <button onClick={() => removeFromWatchlist(symbol)}>
                      Remove
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
